import React, { Component } from 'react';
import axios from 'axios';

export default class Explore extends Component {
    constructor(props) {
        super(props);

        this.onChangeRank = this.onChangeRank.bind(this);
        this.onChangeCountry = this.onChangeCountry.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onChangeSongArtist = this.onChangeSongArtist.bind(this);
        this.onChangeSongTitle = this.onChangeSongTitle.bind(this);
        this.onSearch = this.onSearch.bind(this);

        this.state = {
            rank: "",
            country: "",
            date: "",
            song_artist: "",
            song_title: "",
        };
    };

    onChangeRank(e) {
        this.setState({
            rank: e.target.value,
        });
    };

    onChangeCountry(e) {
        this.setState({
            country: e.target.value,
        });
    };

    onChangeDate(e) {
        this.setState({
            date: e.target.value,
        });
    };

    onChangeSongArtist(e) {
        this.setState({
            song_artist: e.target.value,
        });
    };

    onChangeSongTitle(e) {
        this.setState({
            song_title: e.target.value,
        });
    };

    onSearch(e) {
        e.preventDefault();

        const newSearch = {
            rank: this.state.rank,
            country: this.state.country,
            date: this.state.date,
            song_artist: this.state.song_artist,
            song_title: this.state.song_title,
        };

        axios
            .post("http://localhost:5000/posts/explore", newSearch);

        this.setState({
            rank: "",
            country: "",
            date: "",
            song_artist: "",
            song_title: "",
        });
    };

    render() {
        return (
            <thead>
                <tr>
                    <form onSubmit={this.onSubmit}>
                        <th>
                            <div className="form-group">
                                <input
                                    type="text"
                                    className="form-control"
                                    value={this.state.rank}
                                    onChange={this.onChangePersonName}
                                />
                            </div>
                        </th>
                        <div className="form-group">
                            <label>Person's position: </label>
                            <input
                                type="text"
                                className="form-control"
                                value={this.state.person_position}
                                onChange={this.onChangePersonPosition}
                            />
                        </div>
                        <div className="form-group">
                            <div className="form-check form-check-inline">
                                <input
                                    className="form-check-input"
                                    type="radio"
                                    name="priorityOptions"
                                    id="priorityLow"
                                    value="Intern"
                                    checked={this.state.person_level === "Intern"}
                                    onChange={this.onChangePersonLevel}
                                />
                                <label className="form-check-label">Intern</label>
                            </div>
                            <div className="form-check form-check-inline">
                                <input
                                    className="form-check-input"
                                    type="radio"
                                    name="priorityOptions"
                                    id="priorityMedium"
                                    value="Junior"
                                    checked={this.state.person_level === "Junior"}
                                    onChange={this.onChangePersonLevel}
                                />
                                <label className="form-check-label">Junior</label>
                            </div>
                            <div className="form-check form-check-inline">
                                <input
                                    className="form-check-input"
                                    type="radio"
                                    name="priorityOptions"
                                    id="priorityHigh"
                                    value="Senior"
                                    checked={this.state.person_level === "Senior"}
                                    onChange={this.onChangePersonLevel}
                                />
                                <label className="form-check-label">Senior</label>
                            </div>
                        </div>
                        <div className="form-group">
                            <input
                                type="submit"
                                value="Create person"
                                className="btn btn-primary"
                            />
                        </div>
                    </form>
                </tr>
            </thead>
        );
    };
};
