import React, { Component } from "react";
import axios from "axios";
import { Bar, Radar } from "react-chartjs-2";
import { Container, Row, Col } from "react-bootstrap";

export default class Analytics extends Component {
  constructor(props) {
    super(props);

    this.onChangeRankF1 = this.onChangeRankF1.bind(this);
    this.onChangeCountryF1 = this.onChangeCountryF1.bind(this);
    this.onChangeDateF1 = this.onChangeDateF1.bind(this);
    this.onGoF1 = this.onGoF1.bind(this);
    this.onChangeCountryF2 = this.onChangeCountryF2.bind(this);
    this.onGoF2 = this.onGoF2.bind(this);

    this.state = {
      F1Data: {
        labels: [],
        datasets: [],
      },
      F2Data: {
        labels: [],
        datasets: [],
      },
      F3Data: {
        labels: [],
        datasets: [],
      },
      F4Data: {
        labels: [],
        datasets: [],
      },
      F5Data: {
        labels: [],
        datasets: [],
      },
      F6Data: {
        labels: [],
        datasets: [],
      },
      rankF1: "",
      countryF1: "Global",
      dateF1: "",
      countryF2: "Global",
    };
  }

  onChangeRankF1(e) {
    this.setState({ rankF1: e.target.value });
  }
  onChangeCountryF1(e) {
    this.setState({ countryF1: e.target.value });
  }
  onChangeDateF1(e) {
    this.setState({ dateF1: e.target.value });
  }
  onChangeCountryF2(e) {
    this.setState({ countryF2: e.target.value });
  }

  onGoF1(e) {
    if (this.state.rankF1 === "" || this.state.dateF1 === "")
      alert("Error: please fill out all fields.");
    else {
      const searchTerm = {
        rank: this.state.rankF1,
        country: this.state.countryF1,
        date: this.state.dateF1,
      };

      axios.post("http://localhost:5000/posts/analytics/f1", searchTerm);

      this.setState({
        rankF1: "",
        countryF1: "Global",
        dateF1: "",
      });

      this.updateF1Chart();
    }
  }

  onGoF2(e) {
    axios.post(
      "http://localhost:5000/posts/analytics/f2",
      this.state.countryF2
    );

    this.setState({
      countryF2: "Global",
    });

    this.updateF2Chart();
  }

  componentDidMount() {
    this.updateF1Chart();
    this.updateF2Chart();
    this.updateF3Chart();
    this.updateF4Chart();
    this.updateF5Chart();
    this.updateF6Chart();
  }

  async updateF1Chart() {
    let F1Data = { labels: [], average: [], data: [] };

    //Need to pass in labels, average, and data
    await fetch("http://localhost:5000/posts/analytics/f1")
      .then((res) => res.json())
      .then((data) => {
        F1Data = data;
      });

    this.setState({
      F1Data: {
        labels: F1Data.labels,
        datasets: [
          {
            label: "Chart's Average",
            data: F1Data.average,
            backgroundColor: ["rgba(30, 215, 96, 0.1)"],
