import React, { Component } from 'react';

export default class App extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            messages: []
        }
    }

    componentDidMount(){
      fetch('http://localhost:5000/posts')
      .then(res => res.json())
      .then(messages => this.setState({messages}));
    }

    render() {
        return (
            <div className="App">
                 <ul>
                     {this.state.messages.map(m =>
                         <li>{m.message}</li>
                        )}
                 </ul>
            </div>
        );
    }
}
